import requestsimport csvdef CFC_parse_link(url, cfcNo):    csv_filename = f"data/CFC-{cfcNo}-data-parsed.csv"    # Define a list to store the data dictionaries    data_list = []    # Fetch the data from the URL    response = requests.get(url)    # Check if the request was successful    if response.status_code == 200:        # Split the response content into lines        lines = response.text.splitlines()        # Initialize column names        columns = None        # Iterate through the lines        for line in lines:            # Skip lines starting with "#"            if line.startswith("#"):                continue            # Split the line into values            values = line.strip().split()            # If columns are not defined yet, set them based on the current line            if columns is None:                columns = values            else:                # Create a dictionary for the current line                data_dict = {}                for i in range(len(columns)):                    data_dict[columns[i]] = values[i]                # Append the dictionary to the data list                data_list.append(data_dict)        # Write the parsed data to a CSV file        with open(csv_filename, mode="w", newline="") as csvfile:            writer = csv.DictWriter(csvfile, fieldnames=columns)            writer.writeheader()            writer.writerows(data_list)        print(f"CSV file '{csv_filename}' has been created successfully.")    else:        print("Failed to fetch data from the URL. Status code:", response.status_code)CFC_parse_link("https://gml.noaa.gov/aftp/data/hats/cfcs/cfc11/combined/HATS_global_F11.txt", 11)CFC_parse_link("https://gml.noaa.gov/aftp/data/hats/cfcs/cfc12/combined/HATS_global_F12.txt", 12)CFC_parse_link("https://gml.noaa.gov/aftp/data/hats/cfcs/cfc113/combined/HATS_global_F113.txt", 13)